import './ListaT.css';

function ListaT() {
    
    const [tareas, setTareas] = useState([
        {id: 1, texto: "Lavar los platos", completado:false},
    ])    

    const [nuevaTarea, setNuevaTarea] = useState('')

    const agregarTarea = () => {
        if(nuevaTarea.trim() !== ''){
            const nuevaLista = [...tareas, {id: tareas.lenght + 1, texto: nuevaTarea, completado: false}];
            setTareas(nuevaLista)
            setNuevaTarea('')
        }
    }
    return (
    <div className = "listaTareas">
        <h1>Tareas:</h1>
        <div className="Tareas">
            <div className='listaTarea'>
            <input type="checkbox"/>Lavar los Platos
            </div>
        </div>
        <input type="text" id="agregar" placeholder="Ingrese su tarea aqui"></input>
        <button onClick={ () =>{
            const ultimoElemento = document.getElementsByClassName('listaTarea')
            const nuevaTarea = {
                tarea: document.getElementById('agregar').value,
                completado: false
            }
            
            document.body.insertBefore(nuevaTarea,ultimoElemento);
        } }>Agregar Tarea</button>
        <button onClick={ () =>{
            const lista = document.getElementById('Tareas')
            const tarea = document.getElementById('agregar').value
            var borrarTarea = document.getElementsByClassName('Tareas')
            
            
        } }>Borrar Ultima Tarea</button>
    </div>
   
    );
}

export default ListaT;
