import React, { useState, useEffect } from 'react';
import './Style.css';

function Nota() {
    const [contenido, setContenido] = useState('');
    const [temporizador, setTemporizador] = useState(null);

    const manejarCambio = (evento) => {
        const nuevoContenido = evento.target.value;
        setContenido(nuevoContenido);

        // Limpia el temporizador anterior si existe
        if (temporizador) {
            clearTimeout(temporizador);
        }

        // Configura un nuevo temporizador para guardar la nota después de 2 segundos
        const nuevoTemporizador = setTimeout(() => {
            guardarNota(nuevoContenido);
        }, 2000);

        setTemporizador(nuevoTemporizador);
    };

    const guardarNota = (contenido) => {
        console.log("Nota guardada:", contenido);
        // Aquí podrías hacer una llamada a una API para guardar el contenido en una base de datos
    };

    return (
        <div className="container">
            <div className="notasContainer">
                <h1>Aplicación de Notas</h1>
                <textarea
                    placeholder="Escribe tu nota aquí..."
                    value={contenido}
                    onChange={manejarCambio}
                    rows="10"
                    cols="50"
                />
            </div>
        </div>
    );
}

export default Nota;
