import React, { useState, useEffect } from 'react';
import "./Style.css"
function Botones() {  
    const [color, setColor] = useState("#2d2d2d");

    useEffect(() => {
        document.body.style.backgroundColor = color;
    }, [color]);

    function cambiarFondo(actualColor) {
        if (actualColor === 1) {
            setColor('#a29898');
        } else if (actualColor === 2) {
            setColor('#ccc');
        } else if (actualColor === 3) {
            setColor('#2d2d2d');
        } else if (actualColor === 4) {
            setColor('#000');
        }
    }

    let clickTimer = null;

    function alerta(event) {
        if (event.type === "click") {
            clickTimer = setTimeout(() => {
                alert('Clic');
            }, 200); 
        } else if (event.type === "dblclick") {
            clearTimeout(clickTimer); 
            alert('Doble clic');
        }
    }
    function nombre(event) {
        if (event.type === "click") {
            document.title = "React";
        } else if (event.type === "dblclick") {
            document.title = "App"; 
        }
    }
    function fondo(event) {
        const button = event.target; // Obtenemos el bot칩n que dispara el evento
    
        if (event.type === "click") {
            button.style.backgroundColor = "#4CAF50"; // Color verde para clic simple
        } else if (event.type === "dblclick") {
            button.style.backgroundColor = "#f44336"; // Color rojo para doble clic
        }
    }
    function arriba(event) {
        if (event.type === "click") {
            // Desplazar hacia arriba del contenido visible
            window.scrollTo({ top: 50, behavior: 'smooth' });
        } else if (event.type === "dblclick") {
            // Desplazar al principio absoluto de la p치gina
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }
    }
    

    return (
        <div className="container">
            <div className="botones-contenedor">
                <button className="button-56" onClick={() => cambiarFondo(1)} onDoubleClick={() => cambiarFondo(2)}>
                    Modo Claro
                </button>
                <button className="button-56" onClick={() => cambiarFondo(3)} onDoubleClick={() => cambiarFondo(4)}>
                    Modo Oscuro
                </button>
                <button className="button-56" onClick={alerta} onDoubleClick={alerta}>
                    Alerta
                </button>
                <button className="button-56" onClick={nombre} onDoubleClick={nombre}>
                    Cambiar Nombre
                </button>
                <button className="button-56" onClick={fondo} onDoubleClick={fondo}>
                    Fondo
                </button>
                <button className="button-56" onClick={arriba}>
                    Arriba
                </button>
            </div>
        </div>
    );
}

export default Botones;
