import React, { useState, useEffect } from 'react';
import "./Style.css"
function Botones() {  
    const [color, setColor] = useState("#2d2d2d");
    const [contador, setContador] = useState(0);

    // Cambiar el fondo del body basado en el color
    useEffect(() => {
        document.body.style.backgroundColor = color;
    }, [color]);

    // Cambiar el color de fondo basado en el valor recibido
    function cambiarFondo(actualColor) {
        if (actualColor === 1) {
            setColor('#a29898');
        } else if (actualColor === 2) {
            setColor('#ccc');
        } else if (actualColor === 3) {
            setColor('#2d2d2d');
        } else if (actualColor === 4) {
            setColor('#000');
        }
    }

    // Mostrar una alerta cuando se haga clic
    function alerta() {
        setContador(prevContador => prevContador + 1);
        if (contador === 0) {
            alert('¡Primer clic!');
        } else {
            alert('¡Doble clic!');
        }
    }

    // Funciones placeholders para los otros botones
    function nombre() {
        console.log("Función 'Cambiar Nombre' ejecutada");
    }

    function arriba() {
        console.log("Función 'Arriba' ejecutada");
    }

    function abajo() {
        console.log("Función 'Abajo' ejecutada");
    }

    return (
        <div className="container">
            <div className="botones-contenedor">
                <button className="button-56" role="button" onClick={() => cambiarFondo(1)} onDoubleClick={() => cambiarFondo(2)}>
                    Modo Claro
                </button>
                <button className="button-56" role="button" onClick={() => cambiarFondo(3)} onDoubleClick={() => cambiarFondo(4)}>
                    Modo Oscuro
                </button>
                <button className="button-56" role="button" onClick={alerta} onDoubleClick={alerta}>
                    Alerta
                </button>
                <button className="button-56" role="button" onClick={nombre}>
                    Cambiar Nombre
                </button>
                <button className="button-56" role="button" onClick={arriba}>
                    Arriba
                </button>
                <button className="button-56" role="button" onClick={abajo}>
                    Abajo
                </button>
            </div>
        </div>
    );
}

export default Botones;
