import './Style.css';

function Juego() {
    const numeroRandom = Math.floor(Math.random() * 100);
    function comparaNumero(){
        const intento = document.getElementById("numeroUsuario").value
        const arriba = document.getElementById("arriba")
        const abajo = document.getElementById("abajo")
        if (numeroRandom > intento){
            arriba.style.height = 15;
        }
    }
    return (
        <div class="container">
            <div className='juegoContainer'>
                <button className="flechaArriba" id="arriba">&#9650;</button>
                <div className="numero">
                    <input type="text" id="numeroUsuario"></input>
                </div>
                <button className="flechaAbajo" id="abajo">&#9660;</button>
                <button className='adivinar' onClick={comparaNumero}>Intentar</button>
            </div>
        </div>

    );
}

export default Juego;
